mapper.select:
SELECT m_id, m_pwd, m_name ,m_power
FROM member
WHERE m_id = #{m_id}
AND m_pwd = #{m_pwd}

SELECT count(*)
FROM member
WHERE m_id = #{m_id}

SELECT count(*)
FROM member
WHERE m_email = #{m_email}

mapper.update:
UPDATE member
SET m_pwd = #{m_pwd},m_name = #{m_name},m_tel = #{m_tel},m_address = #{m_address} 
WHERE m_id = #{m_id}

mapper.select:
select m_id 
from member 
where m_email = #{m_email}

select m_pwd 
from member 
where m_id=#{m_id} 
and m_email = #{m_email}
